%标准的DH
% T=[cos(theta) -cos(a)*sin(theta)  sin(a)*sin(theta)    ai*cos(theta);
%    sin(theta) cos(a)*cos(theta)   -sin(a)*cos(theta)   ai*sin(theta);
%     0           sin(a)             cos(a)               di;
%     0           0                   0                   1];
%GTC50  标准的DH建立的可以使用
%抓具末端相对于末端坐标系的位置为[0,-0.0303,0.9921]
%             theta     d        a        alpha       offset 
%     L1 = Link([0        1.195    0.75   pi/2],     'standard');
%     L2 = Link([0         0       1          0],    'standard');
%     L3 = Link([0         0      2.65        0],    'standard');
%     L4 = Link([0         0      1.39        0],    'standard');
%     L5 = Link([0         0      0.1975     -pi/2], 'standard'); 
%     L6 = Link([0      0.1455     0          pi/2],  'standard');
%     L7 = Link([0      0.228      0          0],     'standard');
%GTC50机器人正运动学
syms theta1 theta2 theta3 theta4 theta5 theta6 theta7 
T1=[cos(theta1) 0   sin(theta1)    0.75*cos(theta1);
    sin(theta1) 0   -cos(theta1)   0.75*sin(theta1);
    0           1         0           1.195;
    0           0         0             1];

T2=[cos(theta2) -sin(theta2)  0    cos(theta2);
   sin(theta2)  cos(theta2)   0    sin(theta2);
    0           0             1          0;
    0           0             0         1];

T3=[cos(theta3) -sin(theta3)  0   2.65*cos(theta3);
    sin(theta3) cos(theta3)   0   2.65*sin(theta3);
    0           0             1           0;
    0           0             0           1];

T4=[cos(theta4) -sin(theta4)  0   1.39*cos(theta4);
    sin(theta4) cos(theta4)    0   1.39*sin(theta4);
    0           0             1            0;
    0           0             0            1];

T5=[cos(theta5) 0   -sin(theta5)    0.1975*cos(theta5);
   sin(theta5)  0    cos(theta5)   0.1975*sin(theta5);
    0           -1          0               0;
    0           0           0               1];

T6=[cos(theta6)  0  sin(theta6)    0;
   sin(theta6)  0   -cos(theta6)   0;
    0           1        0        0.1455;
    0           0        0          1];

T7=[cos(theta7) -sin(theta7)   0    0;
   sin(theta7) cos(theta7)     0    0;
    0           0              1    0.228;
    0           0              0    1];

T=T1*T2*T3*T4*T5*T6*T7;
simplify(T)


